var ilnetopt = {
    //业务编号
    id: "",
    //存放业务权限
    permission: {},
    //存放数据
    data: {}
};
//页面加载时进行预处理
$(document).ready(function () {
    if ("undefined" != typeof (ilopt)) {
        ilopt = $.extend(ilnetopt, ilopt);
        ilnetopt = ilopt;
    }
    $.ilnet.load();
});

$.ilnet = {
    load: function () {
        //加载页面按钮权限
        if (ilnetopt.id) {
            $.ajax({
                type: "POST",
                dataType: "json",
                url: "/Page/SetControlsDisplay?rnd=" + Math.random(),
                data: { id: ilnetopt.id },
                async: false,
                success: function (json) {
                    ilopt.permission = json;
                    ilnetopt = ilopt;
                },
                error: function (x) {

                }
            });
        }
    },
}